int findLength(int *A, int ASize, int *B, int BSize){
    int i,j,k,t,count,sameNum;
    int flag;

    i=0;j=0;
    count=0;

    while(i<ASize){
        //用来找到B数组第一个匹配的数据的位置
        while(j<BSize&&A[i]!=B[j])j++;
        k=i+1;
        t=j+1;
        sameNum=0;
        flag=1;
        //求该位置最大匹配长度
        while(k<ASize&&t<BSize&&A[k]==B[t]){
            //用来测试接下来的数据是否都同第一个数一样
            if(A[k]==A[i]&&flag==1){
                sameNum++;
                flag=1;
            }else{
                flag=0;
            }
            k++;t++;
        }
        //比较新求出的最大长度是否比之前求出的长度要长
        if(count<k-i&&j<BSize){
            count = k-i;
            printf("i = %d, count = %d\n", i, count);
        }
        //如果已经求出的长度超过剩下还剩的元素长度，那么就退出测试
        if(k==ASize||count>=ASize-i){break;}
        if(j>=BSize||t==BSize){
            i++;j=0;
        }else if(flag==1){
            j++;
        }else{
            j=j+sameNum+1;
        }
    }
    return count;
}

